mapper.select:
SELECT * FROM permission p INNER JOIN role_perm rp ON
rp.`permId`=p.`id` WHERE rp.`roleId` = #{id}
and permLevel=1

SELECT * FROM permission p INNER JOIN role_perm rp ON
rp.`permId`=p.`id` WHERE rp.`roleId` = #{id}
and permLevel=2

SELECT * FROM permission p INNER JOIN role_perm rp ON
rp.`permId`=p.`id` WHERE rp.`roleId` = #{id}
and permLevel=3
