mapper.select:
SELECT p2.`partClassId`,p1.`id` AS 'cid',p1.`orderNum`,p1.`purchaseOrderId`,p2.`id` AS 'eid',p2.`partType`,p3.`id` AS 'yid',p3.`partName`,p4.*, e1.`id` AS 'buyerId',e1.`name` AS 'buyerName', e2.`id` AS 'auditorId',e2.`name` AS 'auditorName', e3.`id` AS 'consigneeId',e3.`name` AS 'consigneeName', a.`id` AS 'auditstateId',a.`auditStateName` FROM purchaseorderdesc p1 INNER JOIN parttype p2 ON p2.`id`= p1.`partTypeId` INNER JOIN partclassify p3 ON p3.`id` = p2.`partClassId` INNER JOIN purchaseOrder p4 ON p4.id=p1.purchaseOrderId INNER JOIN employee e1 ON p4.`buyer`=e1.`id` INNER JOIN employee e2 ON p4.`auditor`=e2.`id` INNER JOIN employee e3 ON p4.`consignee`=e3.`id` INNER JOIN auditstate a ON p4.`auditStateId`=a.`id`
and p4.auditStateId=#{auditStateId}
and p1.purchaseOrderId=#{purchaseOrderId}
and p4.purchaseTime LIKE CONCAT('%',#{purchaseTime},'%')
and p2.partClassId=#{partType}
GROUP BY p4.`id` limit #{startRow},#{pageSize}

SELECT COUNT(1) FROM ( SELECT p4.* FROM purchaseorderdesc p1 INNER JOIN parttype p2 ON p2.`id`= p1.`partTypeId` INNER JOIN partclassify p3 ON p3.`id` = p2.`partClassId` INNER JOIN purchaseOrder p4 ON p4.id=p1.purchaseOrderId INNER JOIN employee e1 ON p4.`buyer`=e1.`id` INNER JOIN employee e2 ON p4.`auditor`=e2.`id` INNER JOIN employee e3 ON p4.`consignee`=e3.`id` INNER JOIN auditstate a ON p4.`auditStateId`=a.`id`
and p4.auditStateId=#{auditStateId}
and p1.purchaseOrderId=#{purchaseOrderId}
and p4.purchaseTime LIKE CONCAT('%',#{purchaseTime},'%')
and p2.partClassId=#{partType}
GROUP BY p4.`id` )a
