mapper.select:
SELECT qualitycertificate.`id`,qualitycertificate.`deptNo`,admin.`name` adminName,partType.`partType` parttypes, finishedproductstype.`productType` finishedtype,qualitycertificate.`submissionTime`, qualitycertificate.`detectionNum`,qualitycertificate.desc FROM qualitycertificate INNER JOIN admin ON qualitycertificate.`adminPeper`=admin.`id` INNER JOIN partType ON qualitycertificate.`detectionPartTypeId`=partType.`id` INNER JOIN finishedproductstype ON qualitycertificate.`detectionFinishedTypeId`=finishedproductstype.`id`
partType like concat('%',#{partType},'%')
and deptNo=#{deptNo}
limit #{startRow},#{pageSize}

SELECT count(1) FROM qualitycertificate



	
		partType like concat('%',#{partType},'%')
	



	
		and deptNo=#{deptNo}
